import styles from './showInfor.module.scss';
import classNames from 'classnames/bind';
import { useState, useEffect } from "react";
import { fetchData } from '../../../../services/api'; // Import API service
const cx= classNames.bind(styles)

function ShowInfor({ endpoint,onClick }) {
    const [data, setData] = useState([]);
    const [columns, setColumns] = useState([]);

    useEffect(() => {
        async function getData() {
            const result = await fetchData(endpoint);
            if (result && result.length > 0) {
                setColumns(Object.keys(result[0])); // Lấy tên cột tự động
                setData(result);
            }
        }
        getData();
    }, [endpoint]);

    return (
        <div className={cx("wrapper")}>
            
        </div>
    );
}
export default ShowInfor;
