// services/apiClient.js
import axios from "axios";

// Tạo axios instance
const apiClient = axios.create({
    baseURL: "http://127.0.0.1:8000",
});
 const endpoint = {
        products : "http://127.0.0.1:8000/User/",
    }



export default {apiClient, endpoint};
