import { useState } from "react";
import styles from "./addItem.module.scss";
import classNames from "classnames/bind";

const cx = classNames.bind(styles);

function AddItems() {
    const [itemName, setItemName] = useState("");
    const [price, setPrice] = useState("");
    const [category, setCategory] = useState(""); // Loại hàng đang chọn
    const [categories, setCategories] = useState(["Áo", "Quần", "Giày"]); // Danh sách loại hàng
    const [newCategory, setNewCategory] = useState(""); // Loại hàng mới
    const [showNewCategoryInput, setShowNewCategoryInput] = useState(false); // Hiển thị ô nhập mới

    // Khi chọn loại hàng
    const handleCategoryChange = (e) => {
        const selected = e.target.value;
        if (selected === "new") {
            setShowNewCategoryInput(true);
        } else {
            setCategory(selected);
            setShowNewCategoryInput(false);
        }
    };

    // Thêm loại hàng mới
    const handleAddCategory = () => {
        if (newCategory.trim() && !categories.includes(newCategory)) {
            setCategories([...categories, newCategory]);
            setCategory(newCategory);
            setNewCategory("");
            setShowNewCategoryInput(false);
        }
    };

    return (
        <div className={cx("wrapper")}>
            <h2>Thêm sản phẩm</h2>

            <input
                type="text"
                placeholder="Tên sản phẩm"
                value={itemName}
                onChange={(e) => setItemName(e.target.value)}
            />

            <input
                type="number"
                placeholder="Giá sản phẩm"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
            />

            {/* Dropdown chọn loại hàng */}
            <select value={category} onChange={handleCategoryChange}>
                <option value="">Chọn loại hàng</option>
                {categories.map((cat, index) => (
                    <option key={index} value={cat}>
                        {cat}
                    </option>
                ))}
                <option value="new">+ Thêm loại hàng mới</option>
            </select>

            {/* Ô nhập loại hàng mới */}
            {showNewCategoryInput && (
                <div className={cx("new-category")}>
                    <input
                        type="text"
                        placeholder="Nhập loại hàng mới"
                        value={newCategory}
                        onChange={(e) => setNewCategory(e.target.value)}
                    />
                    <button onClick={handleAddCategory}>Xác nhận</button>
                </div>
            )}
        </div>
    );
}

export default AddItems;
