import { useRef } from "react";

const FormWithEnterFocus = () => {
    const nameRef = useRef(null);
    const emailRef = useRef(null);
    const phoneRef = useRef(null);
    const addressRef = useRef(null);

    const handleKeyDown = (e, nextRef) => {
        if (e.key === "Enter") {
            e.preventDefault(); // Ngăn chặn form submit
            nextRef.current.focus();
        }
    };

    return (
        <form>
            <input type="text" ref={nameRef} placeholder="Tên" onKeyDown={(e) => handleKeyDown(e, emailRef)} />
            <input type="email" ref={emailRef} placeholder="Email" onKeyDown={(e) => handleKeyDown(e, phoneRef)} />
            <input type="tel" ref={phoneRef} placeholder="Số điện thoại" onKeyDown={(e) => handleKeyDown(e, addressRef)} />
            <input type="text" ref={addressRef} placeholder="Địa chỉ" />
        </form>
    );
};

export default FormWithEnterFocus;
