import { useState, useRef, useEffect  } from "react";
import styles from "./addItem.module.scss";
import classNames from "classnames/bind";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faXmark,faImage,faTimes } from "@fortawesome/free-solid-svg-icons";
import Preview from "./preview";
import SeeImage from "./seeImage";

const cx = classNames.bind(styles);

function AddItems({ apiUrl }) {
    const initialState = {
        itemName: "",
        sizes: [],
        price: "",
        openModal: false,
        images: [],
        imagePreviews: [],
        description: "",
        preview: false,
        category: "",
        categories: ["Áo", "Quần", "Giày"],
        newCategory: "",
        showNewCategoryInput: false,
        selectedImage: null
    };
    
    const reducer = (state, action) => {
        switch (action.type) {
            case "UPDATE_STATE":
                return { ...state, [action.key]: action.value };
            case "RESET_STATE":
                return initialState;
            case "TOGGLE_SIZE":
                return {
                    ...state,
                    sizes: state.sizes.includes(action.size)
                        ? state.sizes.filter((s) => s !== action.size)
                        : [...state.sizes, action.size]
                };
            case "ADD_IMAGE":
                const newImages = [...state.images, ...action.images];
                const newPreviews = [...state.imagePreviews, ...action.previews];
                return {
                    ...state,
                    images: newImages,
                    imagePreviews: newPreviews,
                    selectedImage: state.selectedImage || newPreviews[0]?.url || null
                };
            case "REMOVE_IMAGE":
                const updatedImages = state.images.filter((_, i) => i !== action.index);
                const updatedPreviews = state.imagePreviews.filter((_, i) => i !== action.index);
                return {
                    ...state,
                    images: updatedImages,
                    imagePreviews: updatedPreviews,
                    selectedImage: updatedPreviews.length > 0 ? updatedPreviews[0].url : null
                };
            case "ADD_CATEGORY":
                return {
                    ...state,
                    categories: [...state.categories, action.newCategory],
                    category: action.newCategory,
                    newCategory: "",
                    showNewCategoryInput: false
                };
            default:
                return state;
        }
    };
    // Thêm item vào database
    // const handleAddItem = async () => {
    //     if (!state.itemName || !state.price || !state.images.length) {
    //         alert("Vui lòng nhập đầy đủ thông tin.");
    //         return;
    //     }

    //     const formData = new FormData();
    //     formData.append("itemName", state.itemName);
    //     formData.append("price", state.price);
    //     formData.append("image", state.images);

    //     try {
    //         const response = await fetch(apiUrl, {
    //             method: "POST",
    //             body: formData
    //         });

    //         const data = await response.json();
    //         if (data.success) {
    //             alert("Thêm sản phẩm thành công!");
    //             setState({
    //                 itemName: "",
    //                 sizes: [],
    //                 price: "",
    //                 openModal: false,
    //                 images: [],
    //                 imagePreviews: [],
    //                 description: "",
    //                 preview: false,
    //                 category: "",
    //                 categories: ["Áo", "Quần", "Giày"],
    //                 newCategory: "",
    //                 showNewCategoryInput: false,
    //                 selectedImage: null
    //             });
    //         } else {
    //             alert("Thêm sản phẩm thất bại!");
    //         }
    //     } catch (error) {
    //         console.error("Lỗi:", error);
    //         alert("Có lỗi xảy ra!");
    //     }
    // };

    return (
        <div className={cx('wrapper')}>
        <div className={cx('btn-openModel')} onClick={handleOpenModel}>
            <FontAwesomeIcon icon={faPlus} /> 
        </div>
    
        {state.openModel && (    
            <div className={cx('modal')}>
                {!state.preview ? (
                    <div className={cx('contents')}>
                        {/* Dropdown chọn loại hàng */}
                        <select className={cx('form-modal')} value={state.category} onChange={handleCategoryChange}>
                            <option value="">Chọn loại hàng</option>
                            {state.categories.map((cat, index) => (
                                <option key={index} value={cat}>{cat}</option>
                            ))}
                            <option value="new">+ Thêm loại hàng mới</option>
                        </select>
    
                        {/* Ô nhập loại hàng mới */}
                        {state.showNewCategoryInput && (
                            <div className={cx('new-category')}>
                                <input
                                    className={cx('form-modal')}
                                    type="text"
                                    placeholder="Nhập loại hàng mới"
                                    value={state.newCategory}
                                    onChange={(e) => setState(prev => ({ ...prev, newCategory: e.target.value }))}
                                />
                                <div className={cx('btn-new-category')} onClick={handleAddCategory}>
                                    <FontAwesomeIcon icon={faPlus} /> Thêm
                                </div>
                            </div>
                        )}
    
                        {/* Tên sản phẩm */}
                        <input
                            className={cx('form-modal')}
                            type="text"
                            placeholder="Tên sản phẩm"
                            value={state.itemName}
                            onChange={(e) => setState(prev => ({ ...prev, itemName: e.target.value }))}
                        />
    
                        {/* Upload ảnh */}
                        <label className={cx('upload-label')}>
                            <FontAwesomeIcon icon={faImage} /> Chọn ảnh
                            <input 
                                type="file" 
                                accept="image/*"  
                                multiple 
                                onChange={handleImageChange} 
                                ref={fileInputRef}
                                hidden 
                            />
                        </label>
    
                        {/* Xem trước ảnh */}
                        <div className={cx('preview-container')}>
                            {state.imagePreviews.map((preview, index) => (
                                <div key={index} className={cx('image-preview-wrapper')} onClick={() => openSeeImage(preview.url)}>
                                    <img src={preview.url} alt={`Xem trước ${index}`} className={cx('image-preview')} />
                                    <button className={cx('btn-remove')} onClick={() => handleRemoveImage(index)}>
                                        <FontAwesomeIcon icon={faTimes} />
                                    </button>
                                </div>
                            ))}
                        </div>
    
                        {/* Chọn size */}
                        <div className={cx('size-options')}>
                            <p>Chọn size:</p>
                            {sizeOptions.map((size) => (
                                <label key={size} className={cx('size-label')}>
                                    <input
                                        className={cx('checkbox')}
                                        type="checkbox"
                                        value={size}
                                        checked={state.sizes.includes(size)}
                                        onChange={() => handleSizeChange(size)}
                                    />
                                    <p className={cx('sizeofcheckbox')}>{size}</p>
                                </label>
                            ))}
                        </div>    
    
                        {/* Giá sản phẩm */}
                        <input
                            className={cx('form-modal')}
                            type="number"
                            placeholder="Giá sản phẩm"
                            value={state.price}
                            onChange={(e) => setState(prev => ({ ...prev, price: e.target.value }))}
                        />
    
                        {/* Mô tả sản phẩm */}
                        <textarea 
                            className={cx('form-modal')}
                            placeholder="Nhập mô tả sản phẩm..."
                            value={state.description}
                            onChange={(e) => setState(prev => ({ ...prev, description: e.target.value }))}
                        />
    
                        {/* Nút tiếp tục */}
                        <button className={cx('btn-preview')} onClick={handlePreview}>Tiếp tục</button>
    
                        {/* Nút đóng modal */}
                        <div className={cx('btn-closeModel')} onClick={handleCloseModal}>
                            <FontAwesomeIcon icon={faXmark} /> 
                        </div>
                    </div> 
                ) : (
                    // Xem trước sản phẩm
                    <Preview 
                        category={state.category} 
                        itemName={state.itemName} 
                        price={state.price} 
                        description={state.description} 
                        sizes={state.sizes} 
                        imagePreviews={state.imagePreviews} 
                        handleEdit={handleEdit} 
                        handleAddItem={handleAddItem} 
                    />
                )}
                <SeeImage imgurl={state.selectedImage} onClose={closeSeeImage} />
            </div>
        )}
    </div>
    
    );
}

export default AddItems;
