import { useState } from "react";
import styles from "./addItem.module.scss";
import classNames from "classnames/bind";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faXmark,faImage,faTimes } from "@fortawesome/free-solid-svg-icons";

const cx = classNames.bind(styles);

function AddItems({ apiUrl }) {
    const [itemName, setItemName] = useState("");  // Tên sản phẩm
    const [sizes, setSizes] = useState("");
    const [price, setPrice] = useState("");  // Giá sản phẩm
    const [message, setMessage] = useState(""); // Thông báo trạng thái
    const [openModel, setOpenModal] = useState(false);
    const [images, setImages] = useState([]);
    const [imagePreviews, setImagePreviews] = useState([]);
    const [description, setDescription] = useState("")
    const [preview, setPreview] = useState(false);

    const sizeOptions = ["S", "M", "L", "XL", "XXL"];

    const handleOpenModel = () =>{
        setOpenModal(true)
    };
    const handleCloseModal = () => {
        setOpenModal(false);
        setDescription("");
        setImagePreviews([]);
        setImages([]);
        setItemName("");
        setSizes([]);
        setMessage("");
    };
    const handleImageChange = (e) => {
        const files = Array.from(e.target.files); // Chuyển file thành mảng

        if (files.length > 0) {
            // Thêm ảnh vào danh sách cũ thay vì ghi đè
            setImages((prev) => [...prev, ...files]);

            // Tạo ảnh xem trước
            const previews = files.map((file) => URL.createObjectURL(file));
            setImagePreviews((prev) => [...prev, ...previews]);
        }
    };
    const handleRemoveImage = (index) => {
        setImages((prev) => prev.filter((_, i) => i !== index));
        setImagePreviews((prev) => prev.filter((_, i) => i !== index));
    };

    const handleSizeChange = (size) => {
        setSizes((prev) => 
            prev.includes(size) ? prev.filter((s) =>s !==size) :[...prev,size]
    );
    }

    const handlePreview = () => {
        if (!itemName || !price || imagePreviews.length === 0 || sizes.length === 0) {
            setMessage("Vui lòng nhập đầy đủ thông tin.");
            return;
        }
        setPreview(true);
    };

    // Khi nhấn "Chỉnh sửa", quay lại form
    const handleEdit = () => {
        setPreview(false);
    };

    const handleAddItem = async () => {
        if (!itemName || !price || !images) {
            setMessage("Vui lòng nhập đầy đủ thông tin.");
            return;
        }

        const formData = new FormData();
        formData.append("itemName", itemName);
        formData.append("price", price);
        formData.append("image", images);

        try {
            const response = await fetch(apiUrl, {
                method: "POST",
                body: formData,
            });

            const data = await response.json();
            if (data.success) {
                setMessage("Thêm sản phẩm thành công!");
                setItemName("");
                setPrice("");
                setDescription("");
                setImages([]);
                setSizes([]);
                setPreview(false);
                setOpenModal(false);
            } else {
                setMessage("Thêm sản phẩm thất bại!");
            }
        } catch (error) {
            console.error("Lỗi:", error);
            setMessage("Có lỗi xảy ra!");
        }
    };

    return (
        <div className={cx("wrapper")}>
           
                <div className={cx('btn-openModel')} onClick={handleOpenModel}>
                    <FontAwesomeIcon icon={faPlus} /> 
                </div>
           
            {message && <p>{message}</p>}
            {openModel && (    
                <div className={cx('modal')}>
                {!preview ? (
                    <div>
                <div className={cx('btn-closeModel')} onClick={handleCloseModal}>
                    <FontAwesomeIcon icon={faXmark} /> Thêm
                </div>
                
                <input
                    type="text"
                    placeholder="Tên sản phẩm"
                    value={itemName}
                    onChange={(e) => setItemName(e.target.value)}
                />
                <label className={cx("btn-upload")}>
                        <FontAwesomeIcon icon={faImage} /> Chọn ảnh
                        <input type="file" accept="image/*"  multiple onChange={handleImageChange} hidden />
                </label>
                <div className={cx('preview-container')}>
                        {imagePreviews.map((preview, index) => (
                            <div key={index} className={cx("image-preview-wrapper")}>
                                <img src={preview} alt={`Xem trước ${index}`} className={cx("image-preview")} />
                                <button className={cx("btn-remove")} onClick={() => handleRemoveImage(index)}>
                                    <FontAwesomeIcon icon={faTimes} />
                                </button>
                            </div>
                        ))}
                    </div>
                    <div className={cx("size-options")}>
                        <p>Chọn size:</p>
                        {sizeOptions.map((size) => (
                            <label key={size} className={cx("size-label")}>
                                <input
                                    type="checkbox"
                                    value={size}
                                    checked={sizes.includes(size)}
                                    onChange={() => handleSizeChange(size)}
                                />
                                {size}
                            </label>
                        ))}
                    </div>    
                <input
                    type="number"
                    placeholder="Giá sản phẩm"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                />
                <textarea 
                    placeholder="Nhập mô tả sản phẩm..."
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <button className={cx('btn-preview')} onClick={handlePreview}>
                    <FontAwesomeIcon icon={faPlus} /> Thêm
                </button>
            </div> ) : (
                        // ======================= Xem trước sản phẩm =======================
                        <div className={cx("preview")}>
                            <h2>Xem Trước Sản Phẩm</h2>
                            <p><strong>Tên:</strong> {itemName}</p>
                            <p><strong>Giá:</strong> {price} VND</p>
                            <p><strong>Mô tả:</strong> {description}</p>
                            <p><strong>Size:</strong> {sizes.join(", ")}</p>

                            {/* Hiển thị ảnh */}
                            <div className={cx("image-preview")}>
                            {imagePreviews.map((preview, index) => (
                            <div key={index} className={cx("image-preview-wrapper")}>
                                <img src={preview} alt={`Xem trước ${index}`} className={cx("image-preview")} />
                                <button className={cx("btn-remove")} onClick={() => handleRemoveImage(index)}>
                                    <FontAwesomeIcon icon={faTimes} />
                                </button>
                            </div>
                        ))}
                            </div>

                            <button onClick={handleEdit}>Chỉnh sửa</button>
                            <button onClick={handleAddItem}>Xác nhận thêm</button>
                        </div>
                    )}
                    </div>)}
        </div>
    );
}

export default AddItems;
